import React, { Component } from 'react';
import axios from "axios";
import Todos from "./component/Todo";
import './App.css';
import Header from "./component/header";
import AddTodo from "./component/addtodo";
import uuid from "uuid";
import {BrowserRouter as Router,Route} from "react-router-dom";
import About from "./component/pages/About";




class App extends Component {
  state = {
    Todo : [
      {
        id : uuid.v4() , 
        title:"Frist item",
        desc: "lorem is free decs",
        complet: false
      },
      {
        id : uuid.v4() , 
        title:"two item",
        desc: "lorem is free decs",
        complet: false
      },
      {
        id : uuid.v4() , 
        title:"three item",
        desc: "lorem is free decs",
        complet: false
      },
    ]
  }
  componentDidMount(){
    axios.get('https://jsonplaceholder.typicode.com/todos?_Limit=10')
     .then(r => console.log(r.date))
  }
  makechange = (id)=>{
    this.setState({Todo:this.state.Todo.map((todo)=>{
      if (todo.id === id) {
        todo.complet = !todo.complet;
      }
      return todo;
    })});
  }
  remove = (id) => {
    /* this.setState({ todos: [...this.state.Todo.filter( todo => todo.id !== id)] }); */
    console.log(id);
    this.setState({ Todo: [...this.state.Todo.filter(todo => todo.id !== id)]})
  }
  addtolist = (title)=>{
    const newtodo ={
      id:uuid.v4(),
      title,
      desc:"this.state.Todo.desc",
      complet:false
    }
    this.setState({Todo:[...this.state.Todo , newtodo]})
  }
  render(){
    return(
      <Router>
          <div className="App">
          <header className="App-header">
            <Route exact path="/" render={props=>(
              <React.Fragment>
             <Header />
            <AddTodo addto={this.addtolist} />
           <Todos todos={this.state.Todo} makechange={this.makechange} remove={this.remove} />
              </React.Fragment>
            )}></Route>
            <Route path="/About" component={About}></Route>

          </header>
         </div>
     </Router>

    );
  }
}

export default App;
